import express from "express";
import cors from "cors";

const app = express();
const port = 8000;

app.use(cors())
app.use(express.json());


const users = {
  users_list: [
    {
      id: "xyz789",
      name: "Charlie",
      job: "Janitor"
    },
    {
      id: "abc123",
      name: "Mac",
      job: "Bouncer"
    },
    {
      id: "ppp222",
      name: "Mac",
      job: "Professor"
    },
    {
      id: "yat999",
      name: "Dee",
      job: "Aspring actress"
    },
    {
      id: "zap555",
      name: "Dennis",
      job: "Bartender"
    }
  ]
};

const findUserByName = (name) => {
  return users["users_list"].filter(
    (user) => user["name"] === name
  );
};

const findUserById = (id) =>
  users["users_list"].find((user) => user["id"] === id);

const addUser = (user) => {
  users["users_list"].push(user);
  return user;
};

const generateId = () => {
  return Math.random().toString(36).substring(2, 9);
};

app.post("/users", (req, res) => {
  const userToAdd = req.body;
  const newUser = {
    id: generateId(),
    ...userToAdd
  };
  addUser(newUser);
  res.status(201).send(newUser);
});

app.get("/users/:id", (req, res) => {
  const id = req.params["id"]; //or req.params.id
  let result = findUserById(id);
  if (result === undefined) {
    res.status(404).send("Resource not found.");
  } else {
    res.send(result);
  }
});

app.get("/users", (req, res) => {
  const name = req.query.name;
  if (name != undefined) {
    let result = findUserByName(name);
    result = { users_list: result };
    res.send(result);
  } else {
    res.send(users);
  }
});


app.get("/users", (req, res) => {
  res.send(users);
});

app.get("/", (req, res) => {
  res.send("Hi mom!");
});

app.listen(port, () => {
  console.log(
    `Example app listening at http://localhost:${port}`
  );
});

app.delete("/users/:id", (req, res) => {
  const userId = req.params.id;
  const initialLength = users.users_list.length;
  users.users_list = users.users_list.filter(user => user.id !== userId);
  
  if (users.users_list.length < initialLength) {
    res.status(204).send(); 
  } else {
    res.status(404).send({ message: "User not found." });
  }
});

app.get("/users", (req, res) => {
  const { name, job } = req.query;

  let filteredUsers = users.users_list;

  if (name) {
    filteredUsers = filteredUsers.filter(user => user.name === name);
  }

  if (job) {
    filteredUsers = filteredUsers.filter(user => user.job === job);
  }

  res.send({ users_list: filteredUsers });
});